mapper.update:
update sys_permission
permission_name = #{permissionName}, permission_display_name = #{permissionDisplayName}, description = #{description}, status = #{status}, version = version + 1, secret_token = #{newSecretToken}, type = #{type}, parent_id = #{parentId}, path = #{path}, method = #{method}, sort = #{sort}, update_user_id = #{updateUserId},
where permission_id = #{permissionId} and version = #{version} and secret_token = #{secretToken}
