mapper.update:
update sys_role
role_name = #{roleName}, role_display_name = #{roleDisplayName}, description = #{description}, status = #{status}, version = version + 1, secret_token = #{newSecretToken}, update_user_id = #{updateUserId},
where role_id = #{roleId} and version = #{version} and secret_token = #{secretToken}

mapper.select:
select r.role_id,
       r.role_display_name
from sys_user_role ur
         join sys_role r on ur.role_id = r.role_id
where ur.user_id = #{userId}
  and r.status = #{status}
