mapper.update:
update sys_user
username = #{username}, password = #{password}, nickname = #{nickname}, email = #{email}, status = #{status}, version = version + 1, secret_token = #{newSecretToken}, auth_version = auth_version + 1, update_user_id = #{updateUserId},
where user_id = #{userId} and version = #{version} and secret_token = #{secretToken}
